@use 'igniteui-angular/theming' as *;
@forward 'igniteui-angular/theming';

$palette: $light-material-palette;
$schema: $light-material-schema;

$green-palette: palette(
    $primary: #09f,
    $secondary: #72da67,
    $gray: #fff,
    $surface: #333,
    $info: color($palette, 'info'),
    $success: color($palette, 'success'),
    $warn: color($palette, 'warn'),
    $error: color($palette, 'error'),
);

$gray-btn-color: color($green-palette, 'gray', 800);

/* eslint-disable */
export interface Employee {
    id: number;
    avatar: string;
    name: string;
    email: string;
    company: string;
    position: string;
    work_phone: string;
    mobile_phone: string;
    fax: string;
    street: string;
    city: string;
    post_code: number;
    state: string;
    country: string;
    referred_by: string;
    created_on: Date;
    birthday: Date;
    last_activity: Date;
    next_activity: Date;
    deals?: { Deals: number, Month: number }[];
    deals_won: number;
    deals_lost: number;
    deals_pending: number;
    deals_total: number;
    ratio: number;
    estimated_sales: number;
    actual_sales: number;
