/* eslint-disable no-underscore-dangle */
import {
    AfterViewInit,
    Component,
    ElementRef,
    OnInit,
    QueryList,
    ViewChild
} from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import {
    CloseScrollStrategy,
    ConnectedPositioningStrategy,
    HorizontalAlignment,
    IColumnExportingEventArgs,
    IgxColumnComponent,
    IgxCsvExporterService,
    IgxDateSummaryOperand,
    IgxExcelExporterService,
    IgxGridComponent,
    IgxNumberSummaryOperand,
    IgxSummaryResult,
    IgxToggleDirective,
    OverlaySettings,
    PositionSettings,
    VerticalAlignment,
    CellType,
    GridSelectionMode
} from 'igniteui-angular';
import { data, Employee } from './data';

function formatDate(val: Date) {
    return new Intl.DateTimeFormat('en-US').format(val);
}

class DealsSummary extends IgxNumberSummaryOperand {
    constructor() {
        super();
    }

    public operate(summaries?: any[]): IgxSummaryResult[] {
        const result = super.operate(summaries).filter((obj) => {
            if (obj.key === 'average' || obj.key === 'sum') {
                const summaryResult = obj.summaryResult;
                // apply formatting to float numbers
                if (Number(summaryResult) === summaryResult) {
                    obj.summaryResult = summaryResult.toLocaleString('en-us', { maximumFractionDigits: 2 });
                }
                return obj;
            }
        });
        return result;
    }
}

class EarliestSummary extends IgxDateSummaryOperand {
    constructor() {
        super();
    }

    public operate(summaries?: any[]): IgxSummaryResult[] {
        const result = super.operate(summaries).filter((obj) => {
            if (obj.key === 'earliest') {
                obj.summaryResult = formatDate(obj.summaryResult);
                return obj;
            }
        });
        return result;
    }
}

class SoonSummary extends IgxDateSummaryOperand {
    constructor() {
        super();
    }

    public operate(summaries?: any[]): IgxSummaryResult[] {
        const result = super.operate(summaries).filter((obj) => {
            if (obj.key === 'latest') {
                obj.label = 'Soon';
                obj.summaryResult = formatDate(obj.summaryResult);
                return obj;
            }
        });
        return result;
    }
}

@Component({
    selector: 'app-grid',
    styleUrls: ['./grid-crm.component.scss'],
    templateUrl: './grid-crm.component.html'
})
export class GridCRMComponent implements OnInit, AfterViewInit {

    @ViewChild('grid1', { read: IgxGridComponent, static: true })
    public grid1!: IgxGridComponent;

    @ViewChild('toggleRefHiding') public toggleRefHiding!: IgxToggleDirective;
    @ViewChild('toggleRefPinning') public toggleRefPinning!: IgxToggleDirective;

    @ViewChild('hidingButton') public hidingButton!: ElementRef;
    @ViewChild('pinningButton') public pinningButton!: ElementRef;

    public localData: Employee[] = [];
    public dealsSummary = DealsSummary;
    public earliestSummary = EarliestSummary;
    public soonSummary = SoonSummary;

    public cols!: QueryList<IgxColumnComponent>;
    public hiddenColsLength: number;
    public pinnedColsLength: number;

    public dark = false;

    public searchText = '';
    public caseSensitive = false;
    public selectionMode: GridSelectionMode = 'multiple';

    public _positionSettings: PositionSettings = {
        horizontalDirection: HorizontalAlignment.Left,
        horizontalStartPoint: HorizontalAlignment.Right,
        verticalStartPoint: VerticalAlignment.Bottom
    };

    public _overlaySettings: OverlaySettings = {
        closeOnOutsideClick: true,
        modal: false,
        // eslint-disable-next-line no-underscore-dangle
        positionStrategy: new ConnectedPositioningStrategy(this._positionSettings),
        scrollStrategy: new CloseScrollStrategy()
    };

    constructor(
        private csvExporter: IgxCsvExporterService,
        private excelExporter: IgxExcelExporterService,
        private activatedRoute: ActivatedRoute) {

        const exporterCb = (args: IColumnExportingEventArgs) => {
            if (args.field === 'Deals') { args.cancel = true; }
        };

        this.excelExporter.columnExporting.subscribe(exporterCb);
        this.csvExporter.columnExporting.subscribe(exporterCb);
    }

    public ngOnInit() {
        const employees: Employee[] = data;
        for (const employee of employees) {
            this.getDeals(employee);
        }
        this.localData = employees;
        this.activatedRoute.queryParams.subscribe(params => {
            this.dark = !!params.dark;
        });
    }

    public toggleHiding() {
        this._overlaySettings.target = this.hidingButton.nativeElement;
        this.toggleRefHiding.toggle(this._overlaySettings);
    }

    public getPhoto(cell: CellType) {
        return cell.row.data.avatar;
    }

    public togglePinning() {
        this._overlaySettings.target = this.pinningButton.nativeElement;
        this.toggleRefPinning.toggle(this._overlaySettings);
    }

    public ngAfterViewInit() {
        this.cols = this.grid1.columnList;
        this.hiddenColsLength = this.cols.filter((col) => col.hidden).length;
        this.pinnedColsLength = this.cols.filter((col) => col.pinned).length;
    }

    public toggleVisibility(col: IgxColumnComponent) {
        if (col.hidden) {
            this.hiddenColsLength--;
        } else {
            this.hiddenColsLength++;
        }
        col.hidden = !col.hidden;
    }

    public togglePin(col: IgxColumnComponent, evt: any) {
        if (col.pinned) {
            this.grid1.unpinColumn(col.field);
            this.pinnedColsLength--;
        } else {
            if (this.grid1.pinColumn(col.field)) {
                this.pinnedColsLength++;
            } else {
                // if pinning fails uncheck the checkbox
                evt.checkbox.checked = false;
            }
        }
    }

    public formatDate(val: Date) {
        return new Intl.DateTimeFormat('en-US').format(val);
    }

    public searchKeyDown(ev: KeyboardEvent) {
        if (ev.key === 'Enter' || ev.key === 'ArrowDown' || ev.key === 'ArrowRight') {
            ev.preventDefault();
            this.grid1.findNext(this.searchText, this.caseSensitive);
        } else if (ev.key === 'ArrowUp' || ev.key === 'ArrowLeft') {
            ev.preventDefault();
            this.grid1.findPrev(this.searchText, this.caseSensitive);
        }
    }

    public updateSearch() {
        this.caseSensitive = !this.caseSensitive;
        this.grid1.findNext(this.searchText, this.caseSensitive);
    }

    public clearSearch() {
        this.searchText = '';
        this.grid1.clearSearch();
    }

    public formatValue(val: any): string {
        return val.toLocaleString('en-us', { maximumFractionDigits: 2 });
    }

    public getDeals(employee: Employee): any {
        employee.deals = this.getDealsData();
    }

    public getDealsData(months?: number): any[] {
        if (months === undefined) {
            months = 12;
        }
        const deals: any[] = [];
        for (let m = 0; m < months; m++) {
            const value = this.getRandomNumber(-20, 30);
            // eslint-disable-next-line @typescript-eslint/naming-convention
            deals.push({ Deals: value, Month: m });
        }
        return deals;
    }

    public getRandomNumber(min: number, max: number): number {
        return Math.round(min + Math.random() * (max - min));
    }
}

@use '../../variables' as *;
@import url('https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fontawesome.css');
@import url('https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fa-regular.css');
@import url('https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fa-solid.css');

:host ::ng-deep {
	$light-palette: palette(
        $primary: #09f,
        $secondary: #ffbf00,
        $surface: #fff
    );

	@include palette($light-palette);

	$checkbox-theme: checkbox-theme(
        $fill-color: color($color: 'secondary', $variant: 500),
        $tick-color: contrast-color($color: 'secondary', $variant: 500)
    );

	$summary-theme: grid-summary-theme(
        $background-color: #f0f8fe,
        $result-color: color($color: 'primary', $variant: 500)
    );

	$crm-grid-theme: grid-theme(
        $header-background: #f0f8fe,
        $header-border-color: #dde5eb
    );

	$crm-grid-toolbar: grid-toolbar-theme(
        $background-color: color($color: 'primary', $variant: 500),
        $title-text-color: contrast-color($color: 'primary', $variant: 500)
    );

	$crm-grid-toolbar-button: button-theme(
        $background: color($light-palette, 'primary', 800),
        $hover-background: color($light-palette, 'primary', 900),
        $active-foreground: contrast-color($light-palette, 'primary', 900),
        $focus-foreground: contrast-color($light-palette, 'primary', 900)
    );

	$crm-grid-search-button: button-theme(
        $background: transparent,
        $focus-background: color($light-palette, 'gray', 200),
        $hover-background: color($light-palette, 'gray', 200)
    );

	$crm-input-drop-down: input-group-theme(
        $placeholder-color: color($light-palette, 'gray', 500),
        $idle-text-color: color($light-palette, 'gray', 900)
    );

	.grid__wrapper {
		width: inherit;
		position: relative;
		height: 100%;
		background: transparent;
	}

	.avatar-cell {
		width: 100%;
		display: flex;
		justify-content: center;
	}

	// grid caption styling
	.crm-toolbar-template {
		justify-content: space-between;
		display: flex;
		flex: 1 0 0%;
		align-items: center;
		padding-right: rem(24px);
		border-radius: 2px;

		& span {
			margin-right: auto;
		}

		.igx-button--flat {
			flex-wrap: nowrap;
			white-space: nowrap;
		}

		.igx-button--icon {
			border-radius: 0;
		}

		.logo-propeller {
			display: flex;
			align-items: center;
			justify-content: center;
			margin-right: 10px;
		}
	}

	.sample-flex-container {
		display: flex;
		align-self: center;
		justify-content: flex-end;
		max-width: 500px;
		width: 70%
	}

	@include css-vars($crm-grid-toolbar);

	.igx-grid-toolbar__actions {
		.igx-button--outlined {
			border: none;
		}
	}

	.igx-grid-toolbar__title {
		max-width: 68ch;
	}

	.igx-grid-toolbar__button-space {
		.igx-icon {
			font-size: 1rem;
			display: flex;
			justify-content: center;
			align-items: center;
		}
	}

	.igx-grid-toolbar__custom-content {
		flex: 1 0 0%;
	}

	.igx-grid-toolbar__actions {
		.igx-button--outlined {
			margin-left: 0.5rem;
		}
	}

	.igx-grid-toolbar__actions {
		.igx-button--outlined {
			border: none;
		}
	}

	.igx-grid-toolbar__actions {
		@include css-vars($crm-grid-toolbar-button);
	}

	.crm-sample-toolbar__title {
		margin-right: rem(16px);
		display: flex;
		align-items: center;
		justify-self: flex-start;
		width: 30%;
	}

	.crm-sample-toolbar__ellipsis {
		@include ellipsis();
		opacity: 0;
		animation: slide-fade 1.25s .25s ease-out forwards;
	}

	.igx-input-group--search {
		width: 100%;
	}

	.igx-input-group--search .igx-input-group__bundle {
		height: rem(36px);
		width: 100%;
		overflow: hidden;

		.igx-input-group__bundle-main {
			padding-top: 0.6rem;
			height: 36px;
		}
	}

	.sample-flex-container {
		.igx-input-group,
		.igx-input-group--focused {
			font-size: 13px;
			overflow: hidden;

			igx-icon {
				font-size: 1rem;
				width: 1rem;
				height: 1rem;
			}
		}

		.igx-input-group__input {
			&::placeholder {
				opacity: 1;
			}
		}
	}

	.caseSensitiveButton {
		margin-left: 8px;
	}

	.igx-input-group--search .igx-input-group__bundle .igx-button--icon {
		border-radius: 0;
	}

	.caseSensitiveIcon {
		width: 1.25rem;
		height: 1.25rem;
		font-size: 1.25rem;
	}

	.searchButtons {
		margin-left: 4px;
	}

	.case-sensitive--active {
		background: color($color: 'primary', $variant: 200);
	}

	.igx-grid__th-resize-line {
		background: color($color: 'secondary', $variant: 500);
	}

	.igx-input-group--search .igx-input-group__bundle,
	.igx-input-group--search .igx-input-group__bundle:hover {
		box-shadow: none;
	}


    @include css-vars($crm-input-drop-down);


	.sample-flex-container {
		.igx-input-group,
		.igx-input-group--focused {
			.igx-input-group__bundle {
				transition: all 250ms ease-in-out;
			}

			igx-icon {
				color: color($color: 'primary', $variant: 200);
			}
		}

		.igx-input-group__textarea,
		.igx-input-group__input {
			color: color($color: 'primary', $variant: 100);
		}

		.igx-input-group {
			igx-icon {
				color: contrast-color($color: 'gray', $variant: 900);
			}

			&:hover,
			&:focus,
			&.igx-input-group--focused {
				igx-icon {
					color: color($color: 'gray', $variant: 600);
				}

				.igx-input-group__bundle {
					background: contrast-color($color: 'gray', $variant: 900);
					color: color($color: 'gray', $variant: 600);
				}

				.igx-input-group__input {
					&::placeholder {
						color: color($color: 'gray', $variant: 600);
						opacity: 1;
					}
				}

				.igx-input-group__textarea,
				.igx-input-group__input {
					color: color($color: 'gray', $variant: 600);
				}

				.igx-button--icon {
					&:focus,
					&:active {
						color: color($color: 'gray', $variant: 600);
					}
				}
			}
		}

		.igx-button--icon {
			&:focus,
			&:active {
				color: color($color: 'primary', $variant: 100);
				background: transparent;
			}
		}

		.igx-input-group__input {
			&::placeholder {
				color: contrast-color($color: 'gray', $variant: 900);
				opacity: 1;
			}
		}
	}

	.igx-input-group--search .igx-input-group__bundle {
		background-color: color($color: 'primary', $variant: 800);

		@include css-vars($crm-grid-search-button);
	}

	.grid__wrapper:not(.dark_grid) {
		@include css-vars($crm-grid-theme);
		@include css-vars($summary-theme);
		@include css-vars($checkbox-theme);
	}


    // DARK THEME
    $dark-palette: palette(
        $primary: #09f,
        $secondary: #fff,
        $surface: #222,
    );

    $checkbox-dark-theme: checkbox-theme(
        $fill-color: color($color: 'gray', $variant: 900),
        $tick-color: contrast-color($color: 'gray', $variant: 900)
    );

    $crm-dark-grid-theme: grid-theme(
        $row-selected-background: color($color: 'gray', $variant: 100),
        $row-selected-text-color: contrast-color($color: 'gray', $variant: 100),
        $row-hover-background: color($color: 'gray', $variant: 200),
        $row-hover-text-color: contrast-color($color: 'gray', $variant: 200),
        $row-selected-hover-background: color($color: 'gray', $variant: 200),
        $row-selected-hover-text-color: contrast-color($color: 'gray', $variant: 200),
        $cell-selected-background: color($color: 'gray', $variant: 900),
        $cell-selected-text-color: contrast-color($color: 'gray', $variant: 900),
        $cell-active-border-color: color($color: 'gray', $variant: 900),
    );

    $crm-grid-dark-button: button-theme(
        $foreground: color($color: 'primary', $variant: 500),
        $hover-foreground: color($color: 'primary', $variant: 500),
        $focus-foreground: color($color: 'primary', $variant: 500),
    );

    $crm-grid-dark-progress: progress-linear-theme(
        $track-color: color($color: 'gray', $variant: 500)
    );

	.dark_grid {
        @include palette($dark-palette);
		@include css-vars($checkbox-dark-theme);
		@include css-vars($crm-dark-grid-theme);
		@include css-vars($crm-grid-dark-button);
		@include css-vars($crm-grid-dark-progress);

		.igx-grid-toolbar__actions {
			@include css-vars($crm-grid-toolbar-button);
		}

        .sample-flex-container {
            .igx-input-group,
            .igx-input-group--focused {
                igx-icon {
                    color: color($color: 'primary', $variant: 200);
                }
            }

            .igx-input-group__textarea,
            .igx-input-group__input {
                color: color($color: 'primary', $variant: 100);
            }

            .igx-input-group {
                igx-icon {
                    color: contrast-color($color: 'gray', $variant: 50);
                }

                &:hover,
                &:focus,
                &.igx-input-group--focused {
                    igx-icon {
                        color: color($color: 'gray', $variant: 400);
                    }

                    .igx-input-group__bundle {
                        background: contrast-color($color: 'gray', $variant: 50);
                        color: color($color: 'gray', $variant: 400);
                    }

                    .igx-input-group__input {
                        &::placeholder {
                            color: color($color: 'gray', $variant: 400);
                        }
                    }

                    .igx-input-group__textarea,
                    .igx-input-group__input {
                        color: color($color: 'gray', $variant: 400);
                    }

                    .igx-button--icon {
                        &:focus,
                        &:active {
                            color: color($color: 'gray', $variant: 400);
                        }
                    }
                }
            }

            .igx-input-group__input {
                &::placeholder {
                    color: contrast-color($color: 'gray', $variant: 50);
                }
            }
        }
    }
}

.animated-fan {
	animation: spin-fade 1.5s cubic-bezier(0.21, 1.07, 0.99, 1) forwards;
}

@keyframes spin-fade {
	0% {
		opacity: 0;
		transform: rotate(0);
	}
	100% {
		opacity: 1;
		transform: rotate(270deg);
	}
}

@keyframes slide-fade {
	0% {
		transform: translateX(-5%);
	}
	100% {
		opacity: 1;
		transform: translateX(0);
	}
}

import { AfterViewInit, Directive, Host, Input, NgModule, Optional, OnDestroy, Inject } from '@angular/core';
import { IgxGridBaseDirective, IgxGridComponent, IGX_GRID_BASE } from 'igniteui-angular';

@Directive({
    // eslint-disable-next-line @angular-eslint/directive-selector
    selector: '[igxPreventDocumentScroll]'
})
export class IgxPreventDocumentScrollDirective implements AfterViewInit, OnDestroy {
    private _preventScroll = true;
    private gridBody: HTMLElement;

    /**
     *  A boolean indicating if page scroll should be prevented while mouse wheeling over the grid,
     * when grid scroll has reached top or bottom. Defaults to true.
     * ```html
     * <igx-grid igxPreventDocumentScroll></igx-grid>
     * <igx-grid [igxPreventDocumentScroll]='false'></igx-grid>
     * ```
     */
    @Input('igxPreventDocumentScroll')
    public set preventScroll(val: boolean) {
        if (val === false) { this._preventScroll = false; }
    }

    /**
     * @hidden
     */
    constructor(@Host() @Optional() @Inject(IGX_GRID_BASE) private grid: IgxGridBaseDirective) {
    }

    public ngAfterViewInit() {
        this.gridBody = this.getGridTBody();
        if (this._preventScroll) {
            this.gridBody.addEventListener('wheel', this.preventDocumentScroll, { passive: false });
        }
    }

    public ngOnDestroy() {
        this.gridBody.removeEventListener('wheel', this.preventDocumentScroll);
    }

    /**
     * Prevents scrolling the page, when mouse wheel over the grid body.
     */
    private preventDocumentScroll(event) {
        event.preventDefault();
    }

    private getGridTBody(): HTMLElement {
        return this.grid.tbody.nativeElement;
    }
}

/**
 * @hidden
 */
@NgModule({
    declarations: [IgxPreventDocumentScrollDirective],
    exports: [IgxPreventDocumentScrollDirective]
})

export class IgxPreventDocumentScrollModule { }

