    constructor(private exporter: IgxWorkbookExporterService) {
      exporter.onWorksheetCellExporting.subscribe({ next: (e: IWorksheetCellExportingEventArgs) => {
        if (e.dataColumnHeader == "Order History") {
          var details = <Array<any>>e.cellValue;
          e.cellValue = null;
          var wsOrderDetails: Worksheet;

          // the grid only contains flat data but we need the child data to provide 
          // the values for the sparkline. to do that we'll store those rows manually 
          // on another worksheet
          if (!e.row.workbook.worksheets().exists("OrderDetails")) {
            wsOrderDetails = e.row.workbook.worksheets().add("OrderDetails");
            var headerRow = wsOrderDetails.rows(0);
            headerRow.setCellValue(0, "Customer ID");
            headerRow.setCellValue(1, "Order ID");
            headerRow.setCellValue(2, "Freight");
          } else {
            wsOrderDetails = e.row.workbook.worksheets("OrderDetails");
          }

          var rowIdx = wsOrderDetails.rows().count;
          const startRowIdx = rowIdx;
          for(var detail of details) {
            var detailRow = wsOrderDetails.rows(rowIdx);
            detailRow.setCellValue(0, detail["CustomerID"]);
            detailRow.setCellValue(1, detail["OrderID"]);
            detailRow.setCellValue(2, detail["Freight"]);
            rowIdx++;
          }

          var region = new WorksheetRegion(wsOrderDetails, startRowIdx, 2, rowIdx - 1, 2, false);
          var dataRegion = region.toString(e.row.workbook.cellReferenceMode, true, true, true);

          if (e.row.worksheet.sparklineGroups().count == 0) {
            e.row.worksheet.sparklineGroups().add(SparklineType.Column, e.row.cells(e.worksheetColumnIndex).toString(), dataRegion);
          } else {
            e.row.worksheet.sparklineGroups(0).sparklines().add(e.row.index, e.worksheetColumnIndex, dataRegion);
          }
        }
      }});

      exporter.onWorksheetRowExported.subscribe({ next: (e: IWorksheetRowExportedEventArgs) => {
        e.row.height = 1000;
      }});

      exporter.onWorkbookExportEnded.subscribe({ next: (e: IWorkbookExportEndedEventArgs) => {
        for (var c = e.dataRegion.firstColumn; c <= e.dataRegion.lastColumn; c++) {
          e.worksheet.columns(c).autoFitWidth();
        }
        ExcelFileUtilities.save(e.workbook, e.fileName);
      }});
    }

    public exportGrid() {
      var opt = new IgxWorkbookExporterOptions("grid_sparklines");
      opt.exportAsTable = this.exportAsTable;
      this.exporter.export(this.grid1, opt);
    }
    
    public ngOnInit(): void {
        this.data = this.DATA;
    }
    
